CREATE MEMORY TABLE PUBLIC.CONF(ATTR CHARACTER(50) NOT NULL PRIMARY KEY,VALUE VARCHAR(1024));
CREATE MEMORY TABLE PUBLIC.MESG(ID INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,CRE_STAMP TIMESTAMP WITH TIME ZONE NOT NULL,LAST_DISPLAY_STAMP TIMESTAMP WITH TIME ZONE,DISPLAYCNT INTEGER,SOURCE VARCHAR(20),SENDER VARCHAR(200),MESSAGE VARCHAR(2048));
